package org.wecancoeit.reviews;

import org.springframework.stereotype.Repository;

import java.util.HashMap;
import java.util.Map;
import java.util.Collection;

@Repository

public class ReviewRepository {
    Map<Long, Review> reviewList = new HashMap<>();

        Review Dragoon = new Review(1l, "The Legend of Dragoon", "https://www.google.com/imgres?imgurl=https%3A%2F%2Fupload.wikimedia.org%2Fwikipedia%2Fen%2F3%2F32%2FLegend_of_Dragoon.jpg&imgrefurl=https%3A%2F%2Fen.wikipedia.org%2Fwiki%2FThe_Legend_of_Dragoon&tbnid=-O0Z33JDMQ6jfM&vet=12ahUKEwiGtaiLr4P2AhWAsHIEHTmAAcQQMygAegUIARDaAQ..i&docid=Z-C8UYu5f6YurM&w=256&h=245&itg=1&q=the%20legend%20of%20dragoon&ved=2ahUKEwiGtaiLr4P2AhWAsHIEHTmAAcQQMygAegUIARDaAQ", "RPG", "One of my favorite RPG's(Role-Playing games of all time! With an action combat system that keeps you on your toes, and a difficult final boss  10/10" );
        Review Trigger = new Review(2l, "Chrono Trigger", "data:image/jpeg;base64,", "RPG", " An epic story that spans across timelines, one of the first games I played that had multiple endings! 9/10");
        Review War = new Review(3l, "God of War", "https://www.google.com/imgres?imgurl=https%3A%2F%2Fm.media-amazon.com%2Fimages%2FM%2FMV5BYTYzOTE4MTMtYTA4ZC00ZWVhLWFiNTQtN2M3NDA4NDI4ODMyXkEyXkFqcGdeQXVyNjI2OTgxNzY%40._V1_FMjpg_UX1000_.jpg&imgrefurl=https%3A%2F%2Fwww.imdb.com%2Ftitle%2Ftt0416779%2F&tbnid=XEasxC-jUwGNIM&vet=12ahUKEwio6PO8r4P2AhXkg3IEHRWMCnIQMygVegUIARCHAg..i&docid=rFY1TMFEknDLLM&w=1000&h=1431&q=god%20of%20war&ved=2ahUKEwio6PO8r4P2AhXkg3IEHRWMCnIQMygVegUIARCHAg", "Action/Adventure", "A story about a man seeking redemption turned into revenge a brutal Brutal game with a great story! 10/10");
        Review Borderlands = new Review(4l, "Borderlands 2", "https://www.google.com/imgres?imgurl=https%3A%2F%2Fcdn2.unrealengine.com%2FDiesel%252Fproductv2%252Fborderlands-2%252Fhome%252FEGS_Borderlands2_GearboxSoftware_S5-1360x766-8546dab7d7968b51fae34f402d61c1f9ea1b6891.jpg&imgrefurl=https%3A%2F%2Fwww.epicgames.com%2Fstore%2Fen-US%2Fp%2Fborderlands-2&tbnid=L_XAd23wOLO7wM&vet=12ahUKEwjlv_DHr4P2AhWsr3IEHZOnAS0QMygAegUIARDcAQ..i&docid=rF_FGfC5PIOkfM&w=1360&h=766&q=borderlands%202&ved=2ahUKEwjlv_DHr4P2AhWsr3IEHZOnAS0QMygAegUIARDcAQ", "Shooter", "One of the best shooters I have ever had the pleasure of playing, it combines storytelling and role-playing elements, with a crisp and challenging shooting experiance would recommend to anyone who loves a good story and shooting! 11/10");
        Review Mortal = new Review(5l, "Mortal Kombat", "", "Fighting", "The game that started it all for me, has been a staple if fight games since the 90's 10/10");
        Review Mario = new Review(6l, "Super Mario Bros", "", "Platform", "The very first game I have ever played, it is til this day near and dear to my heart! 8/10");
        public ReviewRepository() {
            reviewList.put(Dragoon.getId(), Dragoon);
            reviewList.put(Trigger.getId(), Trigger);
            reviewList.put(War.getId(), War);
            reviewList.put(Borderlands.getId(), Borderlands);
            reviewList.put(Mortal.getId(), Mortal);
            reviewList.put(Mario.getId(), Mario);
        }




    public ReviewRepository(Review ...reviewsToAdd) {
        for(Review review: reviewsToAdd){
            reviewList.put(review.getId(), review);
        }
    }

    public Review findOne(long id) {
        return reviewList.get(id);
    }

    public Collection<Review> findAll() {
        return reviewList.values();
    }



}
